import React, { useState } from 'react';
import './App.css';
import 'bootstrap/dist/css/bootstrap.css';
// Import Components
import Header from './Components/Header';
import ToDoList from './Components/ToDoList';
import ToDoInput from './Components/ToDoInput';
import Login from './Components/Login';

function App() {
  // Array of objects containing the tasks with a ID and complete status
  const toDoData = [];

  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [token, setToken] = useState("");
  const [loggedIn, setLoggedIn] = useState(false);
  const [toDoList, setToDoList] = useState(toDoData);

  // Functions below sets the state of the username and password to the value that the user has entered
  function handleUserNameChange(e) {
    setUsername(e.target.value)
  };

  function handlePasswordChange(e) {
    setPassword(e.target.value)
  };

  // Function below invoked when the user clicks the login button. The jwt is fetched and stored in state and loggedin set to true
  function handleLoginButtonClick() {
    fetch("http://localhost:8000/login", {
      method: "POST",
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        username: username,
        password: password
      }),
    })
    .then(res => res.json())
    .then(response => {
      JSON.stringify(setLoggedIn(true), setToken(response.token))
      alert('The user has been logged in')
    })
    .catch(error => console.log('Error:', error))
  }



















  // Takes in newTask (input by user) from AddTaskToToDoList component’s state. 
  // Makes a copy of the toDoList so the state is not directly manipulated.
  // The copy is reassigned to a new array, with copy spread in and the new list item tagged on the end
  const addTask = (newTask) => {
    let copy = [...toDoList];
    copy.push({id: toDoList.length + 1, task: newTask, complete: false });    
    setToDoList(copy);
  }

  // Function to toggle the checkbox of the todo item. 
  // A copy of the list is created. The id of the selected task is found and the status of the completed is toggled
  const toggleToDo = (id) => {
    let copy =[...toDoList];
    const todoList = copy.find(todoList => todoList.id === id);
    todoList.complete = !todoList.complete;
    setToDoList(copy);
  }

  // Function clears the completed items from the todoList
  function handleClearCompleted() {
    // alert("Button Clicked");
    const uncompletedToDos = toDoList.filter(todo => !todo.complete);
    setToDoList(uncompletedToDos);
  }



  function handleCreateUserButtonClick() {
    alert("clicked")
  }

  if (!loggedIn) {
    return (
      <Login
        handleUserNameChange={handleUserNameChange}
        handlePasswordChange={handlePasswordChange}
        handleCreateUserButtonClick={handleCreateUserButtonClick}
        handleLoginButtonClick={handleLoginButtonClick}
      />
    )
  } else {
    return (  
      <div className='todoListDiv'>
          <Header />
          {/* Passing toDoList as a prop to toDoList component */}
          <ToDoInput addTask={addTask}/>
          <button onClick={handleClearCompleted}>Clear Completed</button>
          <ToDoList toDoList={toDoList} toggleToDo={toggleToDo}/>
      </div>
    )
  }
};

export default App;

// I used this youtube video: https://www.youtube.com/watch?v=hQAHSlTtcmY : "Learn React in 30 minutes" by Web Dev simplified
// and this https://www.educative.io/blog/react-hooks-tutorial-todo-list react hooks todolist blog to assist me in this task
