import React from "react";
// Import Components
import ToDo from './ToDo';


function ToDoList({ toDoList, toggleToDo, getToDoListFromDatabase }) {
     
    useEffect(() => {
        getToDoListFromDatabase()
    });
    
    return (
        <div>
            {/* Map over the todoList object that was passed down as props to create individual todos. */}
            {toDoList.map(todo => {
                return (
                    // The individual todo passed down as props to the ToDo component.
                    <ToDo key={todo} toggleToDo={toggleToDo} todo={todo} getToDoListFromDatabase={getToDoListFromDatabase}/>
                )
            })}
        </div>
    );
};

export default ToDoList;
