import React from "react";
import {InputGroup} from 'react-bootstrap';


// Display the task from each object from the ToDo component.
function ToDo ({todo, toggleToDo}) {

    // toggleToDo function to the item with the ID of the specific class
    function handleToDoClick() {
        toggleToDo(todo.id);
    }

    return (
        <div>
            <InputGroup>
        <InputGroup.Radio aria-label="Radio button for following text input" />
                <Form.Control checked={todo.complete} onChange={handleToDoClick} aria-label="Text input with radio button" />
                {todo.task}
      </InputGroup>
            <label>
                <input type="checkbox" checked={todo.complete} onChange={handleToDoClick} />
                {todo.task}
            </label>
        </div>
    );
};

<InputGroup>
        <InputGroup.Radio aria-label="Radio button for following text input" />
        <Form.Control aria-label="Text input with radio button" />
      </InputGroup>

export default ToDo;
