import React from "react";
import {InputGroup, Form} from 'react-bootstrap';


// Display the task from each object from the ToDo component.
function ToDo ({todo, toggleToDo}) {

    // toggleToDo function to the item with the ID of the specific class
    function handleToDoClick() {
        toggleToDo(todo.id);
    }

    return (
        <div>
            <label>
            <InputGroup className="mb-3">
        <InputGroup.Checkbox checked={todo.complete} onChange={handleToDoClick} aria-label="Checkbox for following text input" />
        <Form.Control aria-label="Text input with checkbox" value={todo.task}/>
      </InputGroup>
                
            </label>
            
            {/* <label>
                <input type="checkbox" checked={todo.complete} onChange={handleToDoClick} />
                {todo.task}
            </label> */}
        </div>
    );
};


export default ToDo;
